import requestsfrom bs4 import BeautifulSoupimport redef is_valid_job(title, location, description):    keywords = [        "Atendente", "Atendimento ao Cliente", "Assistente de Suporte",        "Analista de Suporte", "Customer Success", "Sucesso do Cliente",        "Assistente Administrativo", "Analista Administrativo", "Assistente de Atendimento",        "Agente de Atendimento", "Profissional de Atendimento Digital",        "Assistente Virtual", "Remote Virtual Assistant", "Analista de Gest√£o de Relacionamento com o Cliente",        "Auxiliar Administrativo", "Suporte ao Cliente", "Suporte T√©cnico",        "Assistente Comercial", "Assistente de Vendas", "Secret√°rio", "Secret√°ria"    ]        location_ok = "brasil" in location.lower() or "brazil" in location.lower()    lang_ok = not re.search(r'[^\x00-\x7F]+', title + description)  # evita idiomas n√£o latinos    keyword_ok = any(k.lower() in (title + description).lower() for k in keywords)    return location_ok and lang_ok and keyword_okdef get_remote_jobs():    url = "https://www.linkedin.com/jobs-guest/jobs/api/seeMoreJobPostings/search?keywords=Remoto&location=Brasil&trk=public_jobs_jobs-search-bar_search-submit&position=1&pageNum=0&f_TPR=r86400&f_WT=2"    headers = {        "User-Agent": "Mozilla/5.0"    }    response = requests.get(url, headers=headers)    soup = BeautifulSoup(response.text, "html.parser")    job_elements = soup.find_all("li")    jobs = []    for job in job_elements:        try:            title = job.find("h3").get_text().strip()            company = job.find("h4").get_text().strip()            location = job.find("span", class_="job-search-card__location").text.strip()            link = job.find("a")["href"]            description = title + " " + company  # Placeholder (LinkedIn n√£o exibe descri√ß√£o nesse ponto)            if is_valid_job(title, location, description):                jobs.append(f"üíº *{title}*\nüè¢ {company}\nüìç {location}\nüîó {link}")        except:            continue    return jobs