import requestsimport osBOT_TOKEN = os.getenv("BOT_TOKEN")CHAT_ID = os.getenv("TELEGRAM_CHAT_ID")sent_jobs = set()def send_to_telegram(message):    if message in sent_jobs:        return    url = f"https://api.telegram.org/bot{BOT_TOKEN}/sendMessage"    data = {        "chat_id": CHAT_ID,        "text": message,        "parse_mode": "Markdown"    }    requests.post(url, data=data)    sent_jobs.add(message)